import React from 'react';

function LogicPanel({ directions, setDirections }) {
  const handleDragStart = (event, direction) => {
    event.dataTransfer.setData('text/plain', direction);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const direction = event.dataTransfer.getData('text/plain');
    setDirections([...directions, direction]);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'space-around' }}>
      <div
        className='border-rounded-lg bg-indigo-200 w-7 h-7 p-1 items-center'
        draggable
        onDragStart={(e) => handleDragStart(e, 'up')}
        onDragOver={handleDragOver}
        onDrop={handleDrop}
      >
        ↑
      </div>
      <div
        className='border-rounded-lg bg-indigo-200 w-7 h-7 p-1 items-center'
        draggable
        onDragStart={(e) => handleDragStart(e, 'right')}
        onDragOver={handleDragOver}
        onDrop={handleDrop}
      >
        →
      </div>
      <div
        className='border-rounded-lg bg-indigo-200 w-7 h-7 p-1 items-center'
        draggable
        onDragStart={(e) => handleDragStart(e, 'down')}
        onDragOver={handleDragOver}
        onDrop={handleDrop}
      >
        ↓
      </div>
      <div
        className='border-rounded-lg bg-indigo-200 w-7 h-7 p-1 items-center'
        draggable
        onDragStart={(e) => handleDragStart(e, 'left')}
        onDragOver={handleDragOver}
        onDrop={handleDrop}
      >
        ←
      </div>
    </div>
  );
}

export default LogicPanel;
