import React, { useState } from 'react';

const LogicPanel = () => {
  const [directions, setDirections] = useState([]);

  const handleDragStart = (event, direction) => {
    event.dataTransfer.setData('text/plain', direction);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleDrop = (event) => {
    event.preventDefault();
    // const direction = event.dataTransfer.getData('text/plain');
    setDirections([...directions, direction]);
  };

  return (
     <div>
      <div className='p-2 border-rounded-lg bg-indigo-500 text-white'>
        <div className='font-bold text-xl'>Logic Pannel:</div>
        <div className="mt-4"
          onDragOver={handleDragOver}
            onDrop={handleDrop}
            id="drop-area">
            {directions.map((direction, index) => (
              <span key={index} className="mr-2">
                {direction}
              </span>
            ))}
          </div>
      </div>
        <div className="flex justify-center space-x-4">
          
          <div
            className="w-8 h-8 flex items-center justify-center border border-gray-300 cursor-move"
            draggable="true"
            onDragStart={(event) => handleDragStart(event, 'Up')}
          >
            ↑
          </div>
          <div
            className="w-8 h-8 flex items-center justify-center border border-gray-300 cursor-move"
            draggable="true"
            onDragStart={(event) => handleDragStart(event, 'Right')}
          >
            →
          </div>
          <div
            className="w-8 h-8 flex items-center justify-center border border-gray-300 cursor-move"
            draggable="true"
            onDragStart={(event) => handleDragStart(event, 'Down')}
          >
            ↓
          </div>
          <div
            className="w-8 h-8 flex items-center justify-center border border-gray-300 cursor-move"
            draggable="true"
            onDragStart={(event) => handleDragStart(event, 'Left')}
          >
            ←
          </div>
          <div
            className="w-8 h-8 flex items-center justify-center border border-gray-300 cursor-pointer"
            onClick={() => setDirections([])}
          >
            Reset
          </div>
          <div className="mt-4"
          onDragOver={handleDragOver}
            onDrop={handleDrop}
            id="drop-area">
            <strong>Directions: </strong>
            {directions.map((direction, index) => (
              <span key={index} className="mr-2">
                {direction}
              </span>
            ))}
          </div>
        </div>
    </div>
  );
};

export default LogicPanel;
