import React, { useEffect, useState } from 'react';
import Grid from './Grid';

const LogicPanel = () => {
  const [directions, setDirections] = useState([]);
  const [robotPosition, setRobotPosition] = useState({ x: 1, y: 0 });
  const [updateddirections, setupdatedDirections] = useState([]);
  const [dropAreas, setDropAreas] = useState(Array(16).fill(null));

  const handleDragStart = (event, direction) => {
    event.dataTransfer.setData('text/plain', direction);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleDrop = (event, index) => {
    event.preventDefault();
    const direction = event.dataTransfer.getData('text/plain');
    const updatedDropAreas = [...dropAreas];
    updatedDropAreas[index] = direction;
    setDropAreas(updatedDropAreas);
    setDirections([...directions, direction]);
  };
  const handleRemoveDirection = (index) => {
    const updatedDirections = [...directions];
    updatedDirections.splice(index, 1);
    setDirections(updatedDirections);
    const updatedDropAreas = [...dropAreas];
    updatedDropAreas[index] = null;
    setDropAreas(updatedDropAreas);
  };
  // const onDirectionsChange=(directions)=>{
  //   setupdatedDirections([...updateddirections,])
  // };
  const handlePlay = () => {
    // Pass the directions to the parent component
    setupdatedDirections([directions])
  };
  
  const cleardir= ()=>{
      setDirections([]);
      setupdatedDirections([]);
      console.log('cleared');
      setDropAreas(Array(16).fill(null));
      setRobotPosition({ x: 0, y: 0 });
      console.log('updated');
      
  }
  
  useEffect(() => {
    

    updateRobotPosition();
  }, []);

  const updateRobotPosition = (direction) => {

      switch (direction) {
        case 'up':
          newPosition.y -= 1;
          break;
        case 'down':
          newPosition.y += 1;
          break;
        case 'left':
          newPosition.x -= 1;
          break;
        case 'right':
          newPosition.x += 1;
          break;
        default:
          break;
      }

      setRobotPosition(newPosition);
    };

  return (
     <div>
      <div className='flex-row lg:flex p-4 py-8 '>
        <div className="px-2 items-center justify-center w-full" >
          
          <Grid robotPosition={robotPosition} />
        </div>
      
        <div className='w-full lg:px-2 py-2 lg:py-0 md:py-2'>
          <div className=' bg-indigo-950 h-40 lg:h-full'>
            <div className='text-white bg-indigo-400 w-full p-2 border-rounded-lg'>
              Instructions Implemented
            </div>
            <div className="flex-wrap text-white">
              
              <div className='p-2 overscroll-contain'>
              {updateddirections.map((direction, index) => (
                <div
                  key={index}
                  className="mb-1"
                >
                  Robot Moved : {direction}
                  {
                    updateRobotPosition(direction)
                  }
                </div>
              ))}
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className='p-2 border-rounded-lg bg-indigo-500 text-white'>
        <div className='font-bold text-xl'>Logic Pannel:</div>
        
          <div className="flex flex-wrap mt-4">
        {dropAreas.map((direction, index) => (
          <div
            key={index}
            className="w-8 h-8 flex bg-white font-bold text-lg text-black items-center justify-center border border-gray-300 m-1 "
            onDragOver={handleDragOver}
            onDrop={(event) => handleDrop(event, index)}
          >
            {direction=='Up' ? <p>↑</p> : ''}
            {direction=='Down' ? <p>↓</p> : ''}
            {direction=='Left' ? <p>←</p> : ''}
            {direction=='Right' ? <p>→</p> : ''}
          </div>
        ))}
      </div>
      </div>
        <div className="mt-5 p-2 bg-indigo-800 flex justify-center space-x-4">
          
          <div
            className="w-8 h-8 flex  bg-white font-bold text-lg text-black items-center justify-center border border-gray-300 cursor-move"
            draggable="true"
            onDragStart={(event) => handleDragStart(event, 'Up')}
          >
            ↑
          </div>
          <div
            className="w-8 h-8 flex  bg-white font-bold text-lg text-black items-center justify-center border border-gray-300 cursor-move"
            draggable="true"
            onDragStart={(event) => handleDragStart(event, 'Right')}
          >
            →
          </div>
          <div
            className="w-8 h-8 flex  bg-white font-bold text-lg text-black items-center justify-center border border-gray-300 cursor-move"
            draggable="true"
            onDragStart={(event) => handleDragStart(event, 'Down')}
          >
            ↓
          </div>
          <div
            className="w-8 h-8 flex  bg-white font-bold text-lg text-black items-center justify-center border border-gray-300 cursor-move"
            draggable="true"
            onDragStart={(event) => handleDragStart(event, 'Left')}
          >
            ←
          </div>
          <div
            className="  bg-white font-bold text-lg text-black flex items-center justify-center border border-gray-300 cursor-pointer"
            onClick={() => 
              {
                cleardir();
              //   setDirections([]);
              // setDropAreas(Array(16).fill(null));
              // handlePlay();
            }}
            
          >
            Reset
          </div>
          <div className="  bg-white font-bold text-lg text-black flex items-center justify-center border border-gray-300 cursor-pointer">
            {/* Logic panel UI */}
            <button onClick={handlePlay}>Execute</button>
          </div>
          
        </div>
    </div>
  );
};

export default LogicPanel;
