import React, { useEffect, useState } from 'react';
import Grid from './Grid';
import LogicPanel from './LogicPanel';
import './App.css';
import logo from './ranchlogo.svg';

function App() {
  const [robotPosition, setRobotPosition] = useState({ x: 1, y: 0 });
  const [directions, setDirections] = useState([]);


  const handleDirectionsChange = (directions) => {
    // Process the directions received from LogicPanel
    console.log('Directions:', directions);
    setDirections(directions);
  };

  useEffect(() => {
    const connection = createConnection(serverUrl, roomId);
    connection.connect();
    return () => {
      connection.disconnect();
    };
  }, [serverUrl, roomId]);

  return (
    <div className='overscroll-contain'>
      <header className=' bg-indigo-800'>
        <div className=' text-white text-lg  p-2'>
          <img src={logo} className="h-8" alt="logo" />
        </div>
      </header>
      <div className='h-screen sm:flex sm:flex-row md:flex-row'>
        <div className='min-w-[30%] p-2'>
          <div className='flex '>
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6">
              <path stroke-linecap="round" stroke-linejoin="round" d="M16.5 3.75V16.5L12 14.25 7.5 16.5V3.75m9 0H18A2.25 2.25 0 0120.25 6v12A2.25 2.25 0 0118 20.25H6A2.25 2.25 0 013.75 18V6A2.25 2.25 0 016 3.75h1.5m9 0h-9" />
            </svg>
            &nbsp;
            Instructions
          </div>

        </div>
        <div className='min-w-[70%]   bg-slate-200 '>
          <div className='flex-row lg:flex p-4 '>
            <Grid robotPosition={robotPosition} />
            <div className='lg:px-2 py-2 md:py-2'>
              <div className=' bg-indigo-300 h-full'>
                <div className='text-white bg-indigo-400 w-full p-2 border-rounded-lg'>
                  Instructions Implemented
                </div>
                <div className="flex-wrap bg-indigo-900 text-white">

                  <div className='p-2 overscroll-contain'>
                  {directions.map((direction, index) => (
                    <div
                      key={index}
                      className="mb-1"
                    >
                      Robot Moved : {direction}
                    </div>
                  ))}
                  </div>
                </div>
              </div>
            </div>
          </div>

          <LogicPanel onDirectionsChange={handleDirectionsChange} />
          {/* <button onClick={handlePlayClick}>Play</button>
          <button onClick={handleResetClick}>Reset</button> */}
        </div>

        {/* <Grid robotPosition={robotPosition} /> */}


      </div>
    </div>
  );
}

export default App;
